mapper.delete:

delete from gateway_log
where req_id = #{reqId,jdbcType=VARCHAR}
  and order_id = #{orderId,jdbcType=VARCHAR}

mapper.select:

select req_id, order_id, accout_id, app_id, service_id, account_req_time, req_time, 
rsp_time, err_code, err_msg
from gateway_log
where req_id = #{reqId,jdbcType=VARCHAR}
  and order_id = #{orderId,jdbcType=VARCHAR}
