mapper.delete:

delete from sms_log
where sms_id = #{smsId,jdbcType=VARCHAR}
  and req_id = #{reqId,jdbcType=VARCHAR}

mapper.update:

update sms_log
set sms_channl_id = #{smsChannlId,jdbcType=VARCHAR},
  sms_req_log = #{smsReqLog,jdbcType=VARCHAR},
  sms_rsp_log = #{smsRspLog,jdbcType=VARCHAR},
  req_time = #{reqTime,jdbcType=TIMESTAMP},
  rsp_time = #{rspTime,jdbcType=TIMESTAMP}
where sms_id = #{smsId,jdbcType=VARCHAR}
  and req_id = #{reqId,jdbcType=VARCHAR}

mapper.select:

select sms_id, req_id, sms_channl_id, sms_req_log, sms_rsp_log, req_time, rsp_time
from sms_log
where sms_id = #{smsId,jdbcType=VARCHAR}
  and req_id = #{reqId,jdbcType=VARCHAR}
